import React, {Component} from 'react';

export default class HomeEdit extends Component {
  constructor(props) {
    super(props);

    this.state={
      home_title:'',
      home_description:''
    }
  }

  onChangeHomeTitle(e) {
    this.setState({
      home_title: e.target.value
    }
    )
  }

  onChangeHomeDescription(e) {
    this.setState({
      home_description: e.target.value
    })
  }

  onSubmit(){
    console.log('home_title: ' + this.state.home_title);
    console.log('home_description: ' + this.state.home_description);
  }

  render() {
    return (
      <div className='mt-5 mb-5 border-bottom'>
        <form>

          <div className='form-group row'>
            <label for='input-title' className='col-form-label col-sm-2'> Titre du site </label>
            <div className='col-sm-10'>
              <input  type='text'
                      id='input-title'
                      className='form-control'
                      placeholder='Titre du site'
                      />
            </div>
          </div>

          <div className='form-group row'>
            <label for='input-description' className='col-form-label col-sm-2'>Phrase d'accroche </label>
            <div className='col-sm-10'>
              <textarea   id='input-description'
                          className='form-control'
                          placeholder='Phrase qui claque'
                          >
              </textarea>
            </div>
          </div>

          <div className='form-group'>
          <input  className='btn btn-primary align-right'
                  value='edit home'
                  type='submit'
                  />
          </div>
        </form>
      </div>
    )
  }
}
