import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Redirect} from 'react-router-dom';
import HomeEdit from './home-edit';
import CompetenceEdit from './competence-edit';
import CVEdit from './cv-edit';
import PortfolioEdit from './portfolio-edit';
import FooterAdmin from './footer-admin';

export default class Admin extends Component {

  constructor() {
    super();
    this.state = {
      loggedIn : false
    }

    this.isLoggedIn = this.isLoggedIn.bind(this);

  }

  componentDidMount() {
    this.isLoggedIn()
  }

  isLoggedIn() {
     axios.get(process.env.REACT_APP_API_URL + 'signin').then(response => {
       console.log('Get user response: ')
       console.log(response.data)
       if (response.data.user) {
         console.log('Get User: There is a user saved in the server session: ')

         this.setState({
           loggedIn: true,
         })
       } else {
         console.log('Get user: no user');
         this.setState({
           loggedIn: false,
         })
       }
     })
   }


  render () {
    return(
      <div className="adminBackground">
        <div className='container text-center'>
          <HomeEdit/>
          <CompetenceEdit/>
          <CVEdit/>
          <PortfolioEdit/>
        </div>
        <FooterAdmin/>
      </div>
    )
  }
}
