import React, {Component} from 'react';
import axios from 'axios';

export default class CompetenceEdit extends Component {

  constructor(props) {
    super(props);

    this.addCompetence = this.addCompetence.bind(this);
    this.onChangeCompetence = this.onChangeCompetence.bind(this);
    this.handleRemove = this.handleRemove.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state={
      competence:[]
    }
  }

  componentDidMount() {
    let that = this;

    axios.get('http://localhost:4000/competence')
      .then(res => {
        res.data.forEach(function(competence) {
          that.setState({competence: [...that.state.competence, competence.competence]})
        })
      })
  }

  addCompetence() {
    this.setState({competence: [...this.state.competence, '']})
  }

  onChangeCompetence(e, index) {
    this.state.competence[index] = e.target.value;

    this.setState({competence: this.state.competence})
  }

  handleRemove(index) {
    this.state.competence.splice(index, 1)

    this.setState({ competence : this.state.competence})
  }

  onSubmit(e) {
    let competences = this.state.competence;
    axios.get('http://localhost:4000/competence')
      .then(res => {
        competences.forEach(function(competence) {
          res.data.forEach(function(dbCompetence) {
            if(dbCompetence.competence == competence) {
              const competenceUpdate = {
                competence : competence
              }
              console.log(competenceUpdate)
              console.log(dbcompetence._id)
              axios.post('http://localhost:4000/competence/update/dbcompetence._id', competenceUpdate)
                .then(res => console.log("Home updated"))
                .catch(function(err) {
                  console.log(err);
                })
            }
          })
        })
      })
  }

  render() {
    return (
      <div className='mt-5 border-bottom'>
        <h2>Mes compétences</h2>
        {
          this.state.competence.map((competence, index) => {
            return(
              <div key={index} className='mb-2'>
                <input  value={competence}
                        onChange={(e) => this.onChangeCompetence(e, index)}
                        />
                <button className='ml-2 btn btn-danger'
                        onClick={() => this.handleRemove(index)}
                        > Supprimer
                </button>
              </div>
            )
          })
        }
        <button className='btn btn-primary mt-2 mb-2'
                onClick={this.addCompetence}
                >Ajouter une compétence
        </button>
        <div>
          <button className='btn btn-success mb-2'
                  onClick={this.onSubmit}
                  >Enregistrer
          </button>
        </div>
      </div>
    )
  }
}
