import React, {Component} from 'react';
import { HashLink as RouterLink } from 'react-router-hash-link';
import { Link, animateScroll as scroll } from "react-scroll";
import $ from "jquery";
import { CSSTransition } from 'react-transition-group';
import Signin from './signin';


export default class Header extends Component {

  constructor(props) {
    super(props);

    this.showLogInForm = this.showLogInForm.bind(this);
    this.removeLogInForm = this.removeLogInForm.bind(this);
    this.showlogButton = this.showlogButton.bind(this);
    this.logOut = this.logOut.bind(this);

    this.state = {
      isTop:true,
      showLogIn:false,
      loggedIn:false
    }
  }

  componentDidMount() {
    document.addEventListener('scroll', () => {
      const isTop= window.scrollY < 10;
      if(isTop !== this.state.isTop) {
        this.setState({isTop: isTop});
        this.handleNavBarAspect()
      }
    })
  }

  handleNavBarAspect() {
    if(this.state.isTop) {
      $(".navbar").removeClass("NavBarScrolling");
      $(".navbar-brand").removeClass("NavBarBrandScrolling");
    } else{
      $(".navbar").addClass("NavBarScrolling");
      $(".navbar-brand").addClass("NavBarBrandScrolling");
    }
  }

  showlogButton() {
    if (this.state.loggedIn) {
      return(
        <div className='collapse navbar-collapse justify-content-end'>
          <button className='nav-link ancre admin-btn' onClick={this.logOut}><i className="fas fa-user-lock" ></i> LOGOUT </button>
        </div>
      )
    } else {
        return(
          <div className='collapse navbar-collapse justify-content-end'>
            <button className='nav-link ancre admin-btn' onClick={this.showLogInForm}><i className="fas fa-user-lock" ></i> ADMIN </button>
          </div>
        )
      }
  }

  logOut() {
    this.setState({loggedIn:false});
  }

  showLogInForm() {
    this.setState({showLogIn:true});
  }

  removeLogInForm() {
    this.setState({showLogIn:false});
  }

  render() {
    return (
      <header>
        <nav className='navbar navbar-expand-lg fixed-top mt-3'>
          <div className="container">
            <RouterLink to="/" className='navbar-brand'> <span className="nom">David</span><span className="prenom">SWIATKIEWIEZ</span> </RouterLink>
            <div className='collapse navbar-collapse ancre'>
              <ul className='navbar-nav'>
                <li className='nav-item'>
                  <Link
                    to='competence'
                    className='nav-link'
                    activeClass="active"
                    spy={true}
                    smooth={true}
                    offset={0}
                    duration= {400}
                    > COMPETENCES
                  </Link>
                </li>
                <li className='nav-item'>
                  <Link
                    to='cv'
                    className='nav-link'
                    activeClass="active"
                    spy={true}
                    smooth={true}
                    offset={0}
                    duration= {400}
                    > CURRICULUM
                  </Link>
                </li>
                <li className='nav-item'>
                  <Link
                    to="portfolio"
                    className='nav-link'
                    activeClass="active"
                    spy={true}
                    smooth={true}
                    offset={0}
                    duration= {400}
                    >PORTFOLIO
                  </Link>
                </li>
                <li className='nav-item '>
                  <Link
                    to='contact'
                    className='nav-link '
                    activeClass="active"
                    spy={true}
                    smooth={true}
                    offset={0}
                    duration= {400}
                    > CONTACT
                  </Link>
                </li>
              </ul>
              {this.showlogButton}

            </div>
          </div>
        </nav>

        <CSSTransition
          in={this.state.showLogIn}
          timeout={300}
          classNames="login"
          unmountOnExit
          >
          <Signin removeLogIn={this.removeLogInForm}/>
        </CSSTransition>
      </header>
    )
  }
}
