import React, {Component} from 'react';

export default class CompetenceEdit extends Component {

  constructor(props) {
    super(props);

    this.addCompetence = this.addCompetence.bind(this);
    this.onChangeCompetence = this.onChangeCompetence.bind(this);
    this.handleRemove = this.handleRemove.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state={
      competence:[]
    }
  }

  addCompetence() {
    this.setState({competence: [...this.state.competence, '']})
  }

  onChangeCompetence(e, index) {
    this.state.competence[index] = e.target.value;

    this.setState({competence: this.state.competence})
  }

  handleRemove(index) {
    this.state.competence.splice(index, 1)

    this.setState({ competence : this.state.competence})
  }

  onSubmit(e) {
    console.log('compétence : ' + this.state.competence)
  }

  render() {
    return (
      <div className='mt-5'>
        {
          this.state.competence.map((competence, index) => {
            return(
              <div key={index}>
                <input  value={competence}
                        onChange={(e) => this.onChangeCompetence(e, index)}
                        />
                <button onClick={() => this.handleRemove(index)}> Supprimer </button>
              </div>
            )
          })
        }
        <button className='btn btn-primary m-2'
                onClick={this.addCompetence}
                >Ajouter une compétence
        </button>
        <div>
          <button className='btn btn-success'
                  onClick={this.onSubmit}
                  >Enregistrer
          </button>
        </div>
      </div>
    )
  }
}
