    this.setState({[e.target.name]: this.state.e.target.value});
  }

  render() {
    return(
      <div className="signin" >
        <div className="signInForm">
            <button type="button" className="close-btn" onClick={this.props.removeLogIn} name="button">x</button>
            <div className='login-input text-center'>
              <i className="fas fa-cogs"></i>
              <h2 className="section-edit-title">Log in</h2>
              <form action="http://localhost:4000/signin" method="post">
                <div className='col-sm-11 '>
                  <input  type='text'
                          id='input-title'
                          name="username"
                          className='form-control input'
                          placeholder='Administrateur'
                          onChange={this.handleChange()}
                          value={this.state.username}
                          />
                </div>
                <div className='col-sm-11 '>
                  <input  type='password'
                          id='input-title'
                          name="password"
                          className='form-control input'
                          value={this.state.password}
                          onChange={this.handleChange()}
                          placeholder='Mot de passe'
                          />
                </div>
                <div className="ok-btn-container">
                  <input type="submit"  className='ok-btn' value="OK" />
                </div>
              </form>
            </div>
        </div>
      </div>
    )
  }
}
