import React, {Component} from 'react';
import { HashLink as RouterLink } from 'react-router-hash-link';
import { Link, animateScroll as scroll } from "react-scroll";
import $ from "jquery";
import './styles/site.css';

export default class Header extends Component {

  changeNavBar() {
    var navBar = $("nav");
    navBar.classList.add("red");
  }

  render() {
    return (
      <header>
        <nav onscroll="changeNavBar()" className='navbar navbar-expand-lg navbar-dark bg-dark fixed-top'>
          <RouterLink to="/" className='navbar-brand'> SWZ </RouterLink>
          <div className='collapse navbar-collapse '>
            <ul className='navbar-nav'>
              <li className='nav-item'>
                <Link
                  to='competence'
                  className='nav-link'
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  > Comp√©tences
                </Link>
              </li>
              <li className='nav-item'>
                <Link
                  to='cv'
                  className='nav-link'
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  > CV
                </Link>
              </li>
              <li className='nav-item'>
                <Link
                  to="portfolio"
                  className='nav-link'
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  >Portfolio
                </Link>
              </li>
              <li className='nav-item '>
                <Link
                  to='contact'
                  className='nav-link '
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  > Contact
                </Link>
              </li>
            </ul>
          </div>
          <div className='collapse navbar-collapse justify-content-end'>
            <RouterLink to='/admin' className='nav-link'> admin </RouterLink>
          </div>
        </nav>

      </header>
    )
  }
}
