import React, {Component} from 'react';

export default class CVEdit extends Component {

  constructor(props) {
    super(props);

    this.onSubmit = this.onSubmit.bind(this);
    this.addExperience = this.addExperience.bind(this);
    this.onChangeExperienceTitle = this.onChangeExperienceTitle.bind(this);
    this.onChangeExperienceDescription = this.onChangeExperienceDescription.bind(this);

    this.state={
      experience : []
    }
  }

  addExperience() {
    this.setState({experience: [...this.state.experience, {title:'', description:''}]})
  }


  onChangeExperienceTitle(e, index) {
    this.state.experience[index].title = e.target.value;

    this.setState({experience:this.state.experience});
  }

  onChangeExperienceDescription(e, index) {
    this.state.experience[index].description = e.target.value;

    this.setState({experience:this.state.experience});
  }

  onSubmit(e) {
    e.preventDefault();

    console.log('CV');
    this.state.experience.map((exp) => {
      console.log('titre :' + exp.title);
      console.log('desc :' + exp.description);
    })
  }

  render() {
    return (
      <div className='mt-5 border-bottom'>
        <h2> Mes expériences </h2>
          {
            this.state.experience.map((exp, index) => {
              return(
                <div key={index} className='mb-2 border-bottom'>
                  <table className='mb-2'>
                    <tbody>
                      <tr>
                        <td>
                          <label htmlFor='newTitle'>Titre du poste</label>
                        </td>
                        <td>
                          <input  type='text'
                                  id='newTitle'
                                  value={exp.title}
                                  onChange={(e) => {this.onChangeExperienceTitle(e, index)}}
                                  />
                        </td>
                      </tr>
                      <tr>
                        <td>
                          <label htmlFor='newDescription'>Description</label>
                        </td>
                        <td>
                          <textArea  type='text'
                                  id='newDescription'
                                  value={exp.description}
                                  onChange={(e) => {this.onChangeExperienceDescription(e, index)}}
                                  >
                          </textArea>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                  <button className='btn btn-danger mb-2'>
                    Supprimer
                  </button>
                </div>
              )
            })
          }
        <div>
          <button className='btn btn-primary'
                  onClick = {this.addExperience}
                  >Ajouter une expérience
          </button>
        </div>
        <div>
          <button className='btn btn-success mt-2 mb-2'
                  onClick = {this.onSubmit}
                  >Enregistrer
          </button>
        </div>
      </div>
    )
  }
}
