import React, {Component} from 'react';
import SaveBtn from './saveBtn';


export default class Contact extends Component {
  constructor(props) {
      super(props);

      this.onChange = this.onChange.bind(this);

      this.state={
        name:'',
        surname:'',
        mail:'',
        message:''
      }
  }

  onChange(e) {
    this.setState({...this.state, [e.target.name]: e.target.value});
  }

  render() {
    return (
      <footer id='contact' className='section text-center'>
        <h2 className="section-title"> Contactez moi </h2>
        <form className='mt-5 container'>
        <div className='d-flex justify-content-center'>
          <input  type='text'
                  name='name'
                  className='form-control col-sm-9 input'
                  placeholder='PrÃ©nom'
                  value={this.state.name}
                  onChange={e => {this.onChange(e)}}
                  />
        </div>
        <div className='d-flex justify-content-center'>
          <input  type='text'
                  name='surname'
                  placeholder='Nom'
                  className='form-control col-sm-9 input mt-3'
                  value={this.state.surname}
                  onChange={e => {this.onChange(e)}}
                  />
        </div>
        <div className='d-flex justify-content-center'>
          <input  type='text'
                  name='mail'
                  placeholder='Adresse mail'
                  className='form-control col-sm-9 input mt-3'
                  value={this.state.mail}
                  onChange={e => {this.onChange(e)}}
                  />
        </div>
        <div className='d-flex justify-content-center'>
          <textarea type='text'
                    name='message'
                    placeholder='Expliquez moi votre projet'
                    className='form-control col-sm-9 input mt-3 mb-5'
                    value={this.state.message}
                    onChange={e => {this.onChange(e)}}
                  >
          </textarea>
        </div>
        <div className='d-flex justify-content-center'>
          <SaveBtn modified={true}/>
        </div>

        </form>
        <h1 className="name-footer">David<span className="surname">SWIATKIEWIEZ</span></h1>
      </footer>
    )
  }
}
