import React, {Component} from 'react';

export default class HomeEdit extends Component {

  constructor(props) {
    super(props);

    this.onChangeHomeTitle = this.onChangeHomeTitle.bind(this);
    this.onChangeHomeDescription = this.onChangeHomeDescription.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state={
      home_title:'',
      home_description:''
    }
  }

  onChangeHomeTitle(e) {
    this.setState({
      home_title: e.target.value
    })
  }

  onChangeHomeDescription(e) {
    this.setState({
      home_description: e.target.value
    })
  }

  onSubmit(e){
    e.preventDefault();

    console.log('home_title: ' + this.state.home_title);
    console.log('home_description: ' + this.state.home_description);

    this.setState({
      home_title:'',
      home_description:''
    })
  }

  render() {
    return (
      <div className='mt-5 border-bottom'>
        <h2>Home</h2>
        <form onSubmit={this.onSubmit} >

          <div className='form-group row'>
            <label htmlFor='input-title' className='col-form-label col-sm-2'> Titre du site </label>
            <div className='col-sm-10'>
              <input  type='text'
                      id='input-title'
                      className='form-control'
                      placeholder='Titre du site'
                      onChange={this.onChangeHomeTitle}
                      />
            </div>
          </div>

          <div className='form-group row'>
            <label htmlFor='input-description' className='col-form-label col-sm-2'>Phrase d'accroche </label>
            <div className='col-sm-10'>
              <textarea   id='input-description'
                          className='form-control'
                          placeholder='Phrase qui claque'
                          onChange={this.onChangeHomeDescription}
                          >
              </textarea>
            </div>
          </div>

          <div className='form-group d-flex justify-content-end'>
          <input  className='btn btn-success'
                  value='Mettre Ã  jour'
                  type='submit'
                  />
          </div>
        </form>
      </div>
    )
  }
}
