import React, {Component} from 'react';
import { HashLink as RouterLink } from 'react-router-hash-link';
import { Link, animateScroll as scroll } from "react-scroll";
import $ from "jquery";

export default class Header extends Component {

  constructor(props) {
    super(props);

    this.state = {
      isTop:true
    }
  }

  componentDidMount() {
    document.addEventListener('scroll', () => {
      const isTop= window.scrollY < 10;
      if(isTop !== this.state.isTop) {
        this.setState({isTop: isTop});
        this.handleNavBarAspect()
      }
    })
  }

  handleNavBarAspect() {
    if(this.state.isTop) {
      $(".navbar").removeClass("NavBarScrolling");
      $(".navbar-brand").removeClass("NavBarBrandScrolling");
    } else{
      $(".navbar").addClass("NavBarScrolling");
      $(".navbar-brand").addClass("NavBarBrandScrolling");
    }
  }


  render() {
    return (
      <header>
        <nav className='navbar navbar-expand-lg fixed-top'>
          <RouterLink to="/" className='navbar-brand'> David SWZ </RouterLink>
          <div className='collapse navbar-collapse ancre'>
            <ul className='navbar-nav'>
              <li className='nav-item'>
                <Link
                  to='competence'
                  className='nav-link'
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  > Comp√©tences
                </Link>
              </li>
              <li className='nav-item'>
                <Link
                  to='cv'
                  className='nav-link'
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  > CV
                </Link>
              </li>
              <li className='nav-item'>
                <Link
                  to="portfolio"
                  className='nav-link'
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  >Portfolio
                </Link>
              </li>
              <li className='nav-item '>
                <Link
                  to='contact'
                  className='nav-link '
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  > Contact
                </Link>
              </li>
            </ul>
          </div>
          <div className='collapse navbar-collapse justify-content-end'>
            <RouterLink to='/admin' className='nav-link ancre'><i class="fas fa-user-lock"></i> admin </RouterLink>
          </div>
        </nav>

      </header>
    )
  }
}
