import React, {Component} from 'react';

export default class Contact extends Component {
  constructor(props) {
      super(props);

      this.onChange = this.onChange.bind(this);

      this.state={
        name:'',
        surname:'',
        mail:'',
        message:''
      }
  }

  onChange(e) {
    this.setState({...this.state, [e.target.name]: e.target.value});
  }

  onSubmit(e) {
    e.preventDefault();
    console.log(this.state)
  }

  render() {
    return (
      <footer id='contact' className='section text-center'>
        <h2 className="section-title"> Contactez moi </h2>

        <div className='container d-flex justify-content-center row'>
          <p className="name-footer col">Tel: <span className="surname">06.77.09.93.06</span></p>
          <p className="name-footer col">Email: <span className="surname">david.swiatkiewiez@gmail.com</span></p>
          <p className="name-footer col">Adresse: <span className="surname">17 rue de l'église, 94300 Vincennes</span></p>
        </div>

        <form className='mt-5 container'>
        <div className='d-flex justify-content-center'>
          <input  type='text'
                  name='name'
                  className='form-control col-sm-9 input'
                  placeholder='Prénom'
                  value={this.state.name}
                  onChange={e => {this.onChange(e)}}
                  />
        </div>
        <div className='d-flex justify-content-center'>
          <input  type='text'
                  name='surname'
                  placeholder='Nom'
                  className='form-control col-sm-9 input mt-3'
                  value={this.state.surname}
                  onChange={e => {this.onChange(e)}}
                  />
        </div>
        <div className='d-flex justify-content-center'>
          <input  type='text'
                  name='mail'
                  placeholder='Adresse mail'
                  className='form-control col-sm-9 input mt-3'
                  value={this.state.mail}
                  onChange={e => {this.onChange(e)}}
                  />
        </div>
        <div className='d-flex justify-content-center'>
          <textarea type='text'
                    name='message'
                    placeholder='Expliquez moi votre projet'
                    className='form-control col-sm-9 input mt-3'
                    value={this.state.message}
                    onChange={e => {this.onChange(e)}}
                  >
          </textarea>
        </div>
        <div className='d-flex justify-content-center'>
          <button className='enregistrer-btn pl-5 pr-5'
                  onClick= {e => {this.onSubmit(e)}}
                  >Envoyer
          </button>
        </div>

        </form>
        <h1 className="name-footer">David<span className="surname">SWIATKIEWIEZ</span></h1>
      </footer>
    )
  }
}
