import React, {Component} from 'react';
import { HashLink as RouterLink } from 'react-router-hash-link';
import { Link, animateScroll as scroll } from "react-scroll";
import $ from "jquery";

export default class Header extends Component {

  changeNavBar() {
    $(function() {
    var header = $(".header");
    $(window).scroll(function() {
        var scroll = $(window).scrollTop();

        if (scroll >= 200) {
            header.removeClass('header').addClass("header-alt");
        } else {
            header.removeClass("header-alt").addClass('header');
        }
    });
  });
  }

  render() {
    return (
      <header>
        <nav className='navbar navbar-expand-lg navbar-dark bg-dark fixed-top'>
          <RouterLink to="/" className='navbar-brand'> SWZ </RouterLink>
          <div className='collapse navbar-collapse '>
            <ul className='navbar-nav'>
              <li className='nav-item'>
                <Link
                  to='competence'
                  className='nav-link'
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  > Comp√©tences
                </Link>
              </li>
              <li className='nav-item'>
                <Link
                  to='cv'
                  className='nav-link'
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  > CV
                </Link>
              </li>
              <li className='nav-item'>
                <Link
                  to="portfolio"
                  className='nav-link'
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  >Portfolio
                </Link>
              </li>
              <li className='nav-item '>
                <Link
                  to='contact'
                  className='nav-link '
                  activeClass="active"
                  spy={true}
                  smooth={true}
                  offset={0}
                  duration= {400}
                  > Contact
                </Link>
              </li>
            </ul>
          </div>
          <div className='collapse navbar-collapse justify-content-end'>
            <RouterLink to='/admin' className='nav-link'> admin </RouterLink>
          </div>
        </nav>

      </header>
    )
  }
}
