import React, {Component} from 'react';
import { Link, animateScroll as scroll } from "react-scroll";
import axios from 'axios';
import '../styles/site.css';

export default class Home extends Component {

  constructor(props) {
    super(props);

    this.state= {
      home_title:'',
      home_description:''
    }
  }

  componentDidMount() {
    axios.get("http://localhost:4000/home")
    .then(res => {
      this.setState({
        home_title:res.data[0].home_title,
        home_description:res.data[0].home_description
      })
      console.log(res.data)
    })
    .catch(function (err) {
      console.log(err);
    })
  }

  render() {
    return (
      <section id='home' className='section'>
        <div className='container text-center'>
          <div className="animation-home">
            <h1 className="site-title">{this.state.home_title}</h1>
            <h2 className='site-sous-titre'>{this.state.home_description}</h2>
          </div>
          <div className="animation-logo">
            <h1 className="logo">DavidSWIATKIEWIEZ</h1>
            <h2 className='site-sous-titre'>Logo time</h2>
          </div>
          <Link
            to='competence'
            spy={true}
            smooth={true}
            offset={0}
            duration= {400}
            ><i class="fas fa-angle-double-down"></i>
          </Link>
        </div>
      </section>
    )
  }
}
