import React, {Component} from 'react';
import axios from 'axios';

export default class Competence extends Component {

  constructor(props) {
    super(props);
    this.state={
      competence:[],
      idCount:0
    }
  }

  componentDidMount() {
    let that = this;

    axios.get('http://localhost:4000/competence')
      .then(res => {
        res.data.forEach(function(competence) {
          that.setState({competence: [...that.state.competence, {value:competence.value, id:competence.id}]})
        });
        that.setState({idCount: res.data.length})
        console.log(this.state)
      })
      .catch(err => {
        console.log('A problem occured')
      });
  }

  render() {

    return (
      <section id='competence' className='section'>
        <div className='container'>
          <h2> Competence </h2>
          <div className='row'>
            {
              this.state.competence.map((competence) => {
                var icon = "fas fa-user";

                return(
                  <div className="m-5 text-center">
                  <i class={icon}></i>
                    <p>{competence.value}</p>
                  </div>
                )
              })
            }
          </div>
        </div>
      </section>
    )
  }
}
